# 创建游标对象
cursor = conn.cursor()


# 创建 recommend_card 表
cursor.execute('''
CREATE TABLE IF NOT EXISTS recommend_card (
    id INT AUTO_INCREMENT PRIMARY KEY,
    season_logo VARCHAR(255),
    season_title VARCHAR(255),
    season_time VARCHAR(255),
    season_status VARCHAR(255),
    game_link VARCHAR(255)
)
''')

# 创建 contest 表
cursor.execute('''
CREATE TABLE IF NOT EXISTS contest (
    id INT AUTO_INCREMENT PRIMARY KEY,
    contest_title VARCHAR(255),
    team1_name VARCHAR(255),
    team1_icon VARCHAR(255),
    team2_name VARCHAR(255),
    team2_icon VARCHAR(255),
    shadow_img VARCHAR(255)
)
''')

# 创建 video 表
cursor.execute('''
CREATE TABLE IF NOT EXISTS video (
    id INT AUTO_INCREMENT PRIMARY KEY,
    video_title VARCHAR(255),
    video_cover VARCHAR(255),
    video_link VARCHAR(255),
    video_count VARCHAR(255),
    video_like VARCHAR(255),
    video_duration VARCHAR(255)
)
''')

